mapper.insert:
    		INSERT INTO notice(
				noticeTitle
			    , noticeContent
			    , noticeDate
			    , DPT_seq
			) VALUES(
				#{noticeTitle}
			    , #{noticeContent}
			    , #{noticeDate}
			    , #{DPT_seq}
			)

mapper.update:
UPDATE notice
SET
	noticeTitle = #{noticeTitle}
	,noticeContent = #{noticeContent}
WHERE 1 = 1
	AND notice_seq = #{notice_seq}
